function checkRight(c) {
    // if score is right the if statement is checking how much points it needs to add
    if(c===x){
        score++;
        hypeLevel++;
        if (hypeLevel < 6) {
            document.getElementById("game-points").innerHTML = "Additional point! [ "+score+" â­ï¸]";
            document.getElementById("scoreProgressLeftShowing").style.height = 100 - hypeLevel*8 +"%";
        } else {
            score++;
            if (hypeLevel < 10) {
                document.getElementById("game-points").innerHTML = "2x points! ðŸ”¥ [ "+score+" â­ï¸]";
                document.getElementById("scoreProgressLeftShowing").style.height = 100 - hypeLevel*10 +"%";
            } else {
                score++;
                document.getElementById("game-points").innerHTML = "3x points! ðŸ”¥ðŸ”¥ [ "+score+" â­ï¸]";
                document.getElementById("scoreProgressLeftShowing").style.height = "0%";
            }
        }
    } else {
        // if you click the wrong one
        i++;
        hypeLevel = 0;
        document.getElementById("game-points").innerHTML = "You missed and lost 1 second from your time!";
        document.getElementById("scoreProgressLeftShowing").style.height = "100%";
    }


    randomRight();
    // checkHypeLevel();
}

